			and cno=#{searchtxt}

select p.pno, p.pname, p.dprice, p.oprice, i.imgname, p.regist, p.deadline,p.stock, p.sellcount from product p, img i
p.pno = i.pno
and pno=#{searchtxt}
and pname like upper('%'|| #{searchtxt}||'%')
and cno=#{searchtxt}
order by pno desc limit #{startRow}, 10

    	select i.imgname, p.pno, p.pname, p.oprice, p.dprice
  		from category c, product p, img i 
    	where c.cno = p.cno 
  		and p.pno = i.pno
    	and p.cno = #{cno} 
    	  
				
				
					
						and pno=#{searchtxt}
					
					
						and pname like upper('%'|| #{searchtxt}||'%')
					
					
						and cno=#{searchtxt}
					
				
			
			
	
			order by p.pno desc
			limit #{startRow}, 10

  		select count(*)
  		from category c inner join product p 
  		on c.cno = p.cno 
  		
  	
  	
       	p.cno = #{cno}
  	
			
				
				and pno=#{searchtxt}
				
				
