				and cno=#{searchtxt}

mapper.update:

update member
set  addr = #{addr}
 	,detailaddr = #{detailaddr}
where no = #{no}


	update product
	set stock = stock - #{bcount}
	    ,sellcount = sellcount + #{bcount}
	where pno = #{pno}
