mapper.update:
update member
 set  
     nickname=#{nickname}
    , name=#{name}
    , addr=#{addr}
    , detailaddr=#{detailaddr}
    , phone=#{phone}
    , pwd=#{pwd}
 where no=#{no}

mapper.select:
select p.pno, p.pname, p.dprice, p.deadline, r.rcontent, i.imgname
from product p, img i, reply r
where p.pno=i.pno and p.pno=r.pno and r.no=#{no}

  	  		
  	  		
  	  		
  	  		select count(bno)
  	  		from product p1, pay p2
  	  		where p1.pno=p2.pno and p2.no=#{no}
  	  		
  		
  				
  				
  					
  						
						
						and p1.pname like CONCAT('%', #{searchtxt}, '%')
						
					
					
						and p2.bno=#{searchtxt}

select p1.pno, p1.pname, p2.bno, p2.bdate, p1.dprice, m.addr, m.detailaddr, p2.shipping, i.imgname from product p1, pay p2, member m, img i where p1.pno=p2.pno and p2.no=m.no and p1.pno=i.pno and p2.no=#{no}
and p1.pname like CONCAT('%', #{searchtxt}, '%')
and p2.bno=#{searchtxt}
order by p2.bno limit #{startRow}, 10

  	  		
  	  		
  	  		
  	  		select count(bno)
  	  		from product p1, pay p2
  	  		where p1.pno=p2.pno and p2.no=#{no} and p1.cno=#{cno}
  	  		
  		
  				
  				
  					
  						
						
						and p1.pname like CONCAT('%', #{searchtxt}, '%')
						
					
					
						and p2.bno=#{searchtxt}

select p1.pno, p1.pname, p2.bno, p2.bdate, p1.dprice, m.addr, m.detailaddr, p2.shipping, i.imgname from product p1, pay p2, member m, img i where p1.pno=p2.pno and p2.no=m.no and p1.pno=i.pno and p2.no=#{no} and p1.cno=#{cno}
and p1.pname like CONCAT('%', #{searchtxt}, '%')
and p2.bno=#{searchtxt}
order by p2.bno limit #{startRow}, 10

select p1.pno, p1.pname, p2.bno, p2.bdate, p1.dprice, p2.no, p1.cno, m.email
from product p1, pay p2, member m
where p1.pno=p2.pno and m.no=p2.no and p2.no=#{no} and p2.bno=#{bno}

  	  		select count(bno)
  	  		from cancel
  	  		where no=#{no}
  			
  					
  						
							and pname like CONCAT('%', #{searchtxt}, '%')
						
						
							and bno=#{searchtxt}

select c.bno, i.imgname, c.pname, c.bdate, c.dprice, c.cdate from cancel c, img i where c.pno=i.pno and c.no=#{no}
and c.pname like CONCAT('%', #{searchtxt}, '%')
and c.bno=#{searchtxt}
order by c.bno limit #{startRow}, 10

  	  		
  	  		select count(bno)
  	  		from cancel
  	  		where no=#{no} and cno=#{cno}
  			
  					
  						
							and pname like CONCAT('%', #{searchtxt}, '%')
						
						
							and bno=#{searchtxt}

select c.bno, i.imgname, c.pname, c.bdate, c.dprice, c.cdate from cancel c, img i where c.pno=i.pno and c.no=#{no} and c.cno=#{cno}
and c.pname like CONCAT('%', #{searchtxt}, '%')
and c.bno=#{searchtxt}
order by c.bno limit #{startRow}, 10
